import axios from 'axios'
import React, { useEffect, useState } from 'react'


// handles the authentication
export default function useAuth(code) {
  const [accessToken, set_access_token] = useState()
  const [refreshToken, set_refresh_token] = useState()
  const [expiresIn, set_expires_in] = useState()

  // 
  useEffect(() => {
    axios.post('http://localhost:3001/login', { // sends HTTP POST to /login endpoint
        code,
    })
    .then(res => { // gets response from /login endpoint of the server
      console.log("hiiiiiiiiii")
      console.log(res.data)
      set_access_token(res.data.access_token)
      set_refresh_token(res.data.refresh_token)
      set_expires_in(res.data.expires_n)
      window.history.pushState({}, null, "/")
    })
    .catch(error => {
      window.location = "/"
      console.error(error)
    }) 
  }, [code])


  useEffect(() => {
    axios
      .post('http://localhost:3001/refresh', { 
        refreshToken,
      })
      .then(res => {
        set_access_token(res.data.access_token)
        set_expires_in(res.data.expires_in)
      })
      .catch(error => {
        console.error(error)
      })

  }, [refreshToken, expiresIn])

  return accessToken
}


