import { useState, useEffect } from "react"
import SpotifyPlayer from "react-spotify-web-playback"

export default function Player({ accessToken, tracks }) {
  const [play, setPlay] = useState(false)
  const [trackUris, setTrackUris] = useState([])



  // const addUri = (uri) => {
  //   setTrackUris([...trackUris, uri])
  // }

  // for (const track of tracks) {
  //   console.log(track)
  //   addUri(track[0].track.uri)
  // }

  // console.log.log(trackUris)
  // useEffect(() => setPlay(true), [trackUris])
  useEffect(() => setPlay(true), [tracks])

  if (!accessToken) return null
  return (
    <SpotifyPlayer
      token={accessToken}
      showSaveIcon
      callback={state => {
        if (!state.isPlaying) setPlay(false)
      }}
      play={play}
      // uris={trackUris ? trackUris : []}
    />
  )
}
