  const [refresh_token, set_refresh_token] = useState()
  const [expires_in, set_expires_in] = useState()

  // 
  useEffect(() => {
    axios.post('http://localhost:3001/login', { // sends HTTP POST to /login endpoint
        code,
    })
    .then(res => {
        console.log(res.data)
        window.history.pushState({}, null, "/")
    })
    .catch(error => {
      window.location = "/"
      console.error(error)
    }) 
  }, [code])
}

