import React from 'react'

// allows user to login and authenticate
const CLIENT_ID = "11e1a8a6d9664d7f9eefd2e7de958a15";

const REDIRECT_URI = "http://localhost:3001"

const SCOPE = ""

const AUTH_URL = `https://accounts.spotify.com/authorize?client_id=${CLIENT_ID}&response_type=code&redirect_uri=${REDIRECT_URI}&state=&scope=streaming%20user-read-private%20user-read-email%20user-library-read%20user-modify-playbackstate%20user-read-playback-state`

const WelcomeMessage = () => {
  return (
  <div className="text-center">
      <h1 className="text-[#8582d9]  w-full text-center">Welcome to Soap Opera! </h1>
      <p className="text-center  " >Don't take too long in the shower! With Soap Opera you pick the playlist and we pick the best songs to limit your water waste</p>
      <a  className = "text-center" href={AUTH_URL}>Login to Spotify</a>
  </div>)
}

export default function Login() {
  return (
    <div>
      {WelcomeMessage()}
    </div>
  )
}
